CREATE TABLE FACULTY (
    FACULTY_ID VARCHAR2(20),
    MAJOR_ID VARCHAR2(20),
    FACULTY_NAME VARCHAR2(20),
    FACULTY_EMAIL VARCHAR2(30),
    FACULTY_POSITION VARCHAR2(20),
    FACULTY_LAB VARCHAR2(30),
    CONSTRAINT PK_FACULTY PRIMARY KEY (FACULTY_ID),
    CONSTRAINT FK_FACULTY FOREIGN KEY (MAJOR_ID) REFERENCES MAJOR(MAJOR_ID)
);

